import "./App.css";
import {
  BrowserRouter as Router,
  Navigate,
  Route,
  Routes,
  useNavigate,
} from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Popup from "./components/Popup";
import ApprovePost from "./pages/ApprovePost";
import {
  createContext,
  useContext,
  useEffect,
  useReducer,
  useState,
} from "react";
import { initialState, reducer } from "./reducers/userReducer";

export const UserContext = createContext();

const Routing = () => {
  const history = useNavigate();
  const { state, dispatch } = useContext(UserContext);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));

    if (user) {
      dispatch({ type: "USER", payload: user });
    } else if (window.location.href == "http://localhost:3000/register") {
      history("/register");
    } else {
      history("/login");
    }
  }, []);

  // console.log(state);

  return (
    <Routes>
      <Route exact path="/login" element={<Login />} />
      <Route exact path="/register" element={<Register />} />
      <Route
        exact
        path="/"
        element={state ? <Home /> : <Navigate to="/login" />}
      />
      <Route
        exact
        path="/approvepost"
        element={
          state && state.userType === 1 ? <ApprovePost /> : <Navigate to="/" />
        }
      />

      {/* <Route exact path="/" element={<Home />} />
      <Route exact path="/approvepost" element={<ApprovePost />} /> */}
    </Routes>
  );
};

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user) {
      dispatch({ type: "USER", payload: user });
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(state));
  }, [state]);


  return (
    <UserContext.Provider value={{ state, dispatch }}>
      <Popup />
      <Router>
        <Routing />
      </Router>
    </UserContext.Provider>
  );
}

export default App;
