import React, { useEffect, useState } from "react";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import Post from "../components/Post";

const Home = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch(process.env.REACT_APP_API_URL + "/api/allPost", {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setData(result);
      });
  }, [data]);

  return (
    <>
      <Navbar />
      <div className="container-fluid gedf-wrapper">
        <div className="row justify_center">
          <div className="col-md-6 gedf-main">
            {/*- \\\\\\\Post*/}
            <div className="card gedf-card">
              <div className="card-header">
                <ul
                  className="nav nav-tabs card-header-tabs"
                  id="myTab"
                  role="tablist"
                >
                  <li className="nav-item">
                    <a
                      className="nav-link active"
                      id="posts-tab"
                      data-toggle="tab"
                      href="#posts"
                      role="tab"
                      aria-controls="posts"
                      aria-selected="true"
                    >
                      Ask your question
                    </a>
                  </li>
                </ul>
              </div>
              <Post />
            </div>
            {/* Post /////*/}
            {/*- \\\\\\\Post*/}
            {data?.map((item) => {
              return (
                <div className="card gedf-card">
                  <div className="card-header">
                    <div className="d-flex justify-content-between align-items-center">
                      <div className="d-flex justify-content-between align-items-center">
                        
                        <div className="ml-2">
                          <div className="h5 m-0">{item.user.name}</div>
                          
                        </div>
                      </div>
                      <div>
                        <i
                          className="bi bi-trash3-fill"
                          // onClick={() => deletpost(item._id)}
                          style={{ cursor: "pointer" }}
                        ></i>
                      </div>
                    </div>
                  </div>
                  <div className="card-body">
                    <div className="text-muted h7 mb-2">
                      <i className="fa fa-clock-o" />
                      {item.createdAt}
                    </div>
                    <a className="card-link" href="#">
                      <h5 className="card-title">{item.name}</h5>
                    </a>
                    <p className="card-text">{item.description}</p>
                  </div>
                  <div className="card-footer">
                    {/* <div className="position-relative comment-box"> */}
                    <form
                    // onSubmit={(e) => {
                    //   e.preventDefault();
                    //   makeComment(e.target[0].value, item._id);
                    // }}
                    >
                      <input
                        type="text"
                        // value={comment}
                        // onChange={(e) => setComment(e.target.value)}
                        className="w-100 border-0 p-3 input-post"
                        placeholder="Add a comment..."
                      />
                      <button
                        className="btn btn-primary position-absolute btn-ig"
                        type="submit"
                      >
                        Post
                      </button>
                    </form>
                    {/* </div> */}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Home;
