import "./App.css";
import Login from "./pages/Login";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./pages/Home";
import Register from "./pages/Register";
import Popup from "./components/Popup";
import ApprovePost from "./pages/ApprovePost";

// Protected Route Component
const ProtectedRoute = ({ component: Component, isAuthenticated, isAdmin, ...rest }) => (
  <Route
    {...rest}
    render={(props) =>
      isAuthenticated && isAdmin ? (
        <Component {...props} />
      ) : isAuthenticated ? (
        <Home {...props} />
      ) : (
        <Redirect to="/login" />
      )
    }
  />
);


function App() {
  const user = JSON.parse(localStorage.getItem("user"));

  const isAuthenticated = user
  const isAdmin = user.userType === 1

  return (
    <Router>
      <Popup />
      <Routes>
        <Route exact path="/login" element={<Login />} />
        <Route exact path="/register" element={<Register />} />

        <Route exact path="/" element={<Home />} isAuthenticated={isAuthenticated}/>
        <Route exact path="/approvepost" element={<ApprovePost />} isAuthenticated={isAuthenticated} isAdmin={isAdmin}/>
      </Routes>
    </Router>
  );
}

export default App;
